// header-client.component.ts
import { Component, OnInit } from '@angular/core';
import { AuthService } from '././../services/auth.service';
import { Router } from '@angular/router';

@Component({
  selector: 'app-header-client',
  templateUrl: './header-client.component.html',
  styleUrls: ['./header-client.component.css']
})
export class HeaderClientComponent implements OnInit {
  currentUser: any;

  constructor(private authService: AuthService, private router: Router) {}

  ngOnInit(): void {
    this.currentUser = this.authService.getUser();
  }

  logout(): void {
    this.authService.logout();
    this.router.navigate(['/login']);
  }
}
<header class="header">
  <div class="header-container">
    <div class="logo">✨ ToDo Project Manager ✨</div>
    <nav class="nav-menu">
      <a routerLink="/" class="nav-link">Home</a>
      <a routerLink="/projects" class="nav-link">My Projects</a>
      <a routerLink="/dashboard-client" class="nav-link">Dashboard</a>
      <a (click)="logout()" class="nav-link">Logout</a>
    </nav>
  </div>
  <p class="tagline">Manage your projects and tasks like a pro!</p>
</header>
