import { Component, OnInit, ChangeDetectorRef } from '@angular/core';
import { DashboardService, Project, Task } from '../../../services/dashboard.service';
import { UserService, User } from '../../../services/user.service';
import { Subject, forkJoin, Observable } from 'rxjs';
import { debounceTime, distinctUntilChanged } from 'rxjs/operators';

@Component({
  selector: 'app-dashboard',
  templateUrl: './dashboard.component.html',
  styleUrls: ['./dashboard.component.css']
})
export class DashboardComponent implements OnInit {

  currentUser: User = { id: 1, nom: 'Admin', prenom: 'Admin', email: 'admin@example.com', role: 'admin' };

  users: User[] = [];
  filteredUsers: User[] = [];
  projects: Project[] = [];

  newProjectTitle = '';
  newProjectDescription = '';
  assignedUserId: number | null = null;
  newProjectTitleError = false;
  newProjectDescriptionError = false;

  searchTerm$ = new Subject<string>();

  constructor(
    private dashboardService: DashboardService,
    private userService: UserService,
    private cd: ChangeDetectorRef
  ) {}

  ngOnInit(): void {
    this.loadUsers();
    this.searchTerm$.pipe(
      debounceTime(300),
      distinctUntilChanged()
    ).subscribe(term => this.filterUsers(term));
  }

  loadUsers() {
    this.userService.getAllUsers().subscribe({
      next: (users) => {
        this.users = users.filter(u => u.role !== 'admin');
        this.filteredUsers = [...this.users];
        this.loadProjects();
      },
      error: (error) => {
        console.error('Erreur lors du chargement des utilisateurs:', error);
      }
    });
  }

  loadProjects() {
    if (this.currentUser.role === 'admin') {
      this.dashboardService.getAllProjects().subscribe({
        next: (projects) => {
          console.log('Projets chargés depuis getAllProjects:', projects);
          this.projects = projects || [];
          // Charger les tâches pour chaque projet
          this.loadTasksForAllProjects();
        },
        error: (error) => {
          console.error('Erreur lors du chargement des projets:', error);
          this.projects = [];
          this.cd.detectChanges();
        }
      });
    } else {
      this.dashboardService.getProjectsByUser(this.currentUser.email).subscribe({
        next: (projects) => {
          console.log('Projets chargés pour l\'utilisateur:', projects);
          this.projects = projects || [];
          // Charger les tâches pour chaque projet
          this.loadTasksForAllProjects();
        },
        error: (error) => {
          console.error('Erreur lors du chargement des projets utilisateur:', error);
          this.projects = [];
          this.cd.detectChanges();
        }
      });
    }
  }

  loadTasksForAllProjects() {
    // Créer un tableau d'observables pour les tâches de chaque projet
    const taskObservables: Observable<Task[]>[] = [];
    const projectIndexMap: { [key: number]: number } = {}; // Pour mapper l'index des projets

    this.projects.forEach((project, index) => {
      if (project.id) {
        projectIndexMap[project.id] = index;
        taskObservables.push(this.dashboardService.getProjectTasks(project.id));
      }
    });

    if (taskObservables.length > 0) {
      forkJoin(taskObservables).subscribe({
        next: (tasksArrays: Task[][]) => {
          tasksArrays.forEach((tasks, observableIndex) => {
            // Trouver l'index du projet correspondant
            const projectId = Object.keys(projectIndexMap).find(
              key => projectIndexMap[parseInt(key)] === observableIndex
            );

            if (projectId) {
              const projectIndex = projectIndexMap[parseInt(projectId)];
              if (this.projects[projectIndex]) {
                this.projects[projectIndex].tasks = tasks || [];
              }
            }
          });
          this.cd.detectChanges();
        },
        error: (error) => {
          console.error('Erreur lors du chargement des tâches:', error);
          this.cd.detectChanges();
        }
      });
    } else {
      this.cd.detectChanges();
    }
  }

  // ... Le reste du code reste inchangé jusqu'à la méthode addTask ...

  addTask(project: Project, taskTitle: string) {
    if (!taskTitle || !taskTitle.trim()) {
      console.log('Titre de tâche vide, abandon');
      return;
    }

    const task: Task = { 
      title: taskTitle.trim(), 
      done: false
    };

    if (project.id) {
      this.dashboardService.addTaskToProject(project.id, task).subscribe({
        next: (savedTask) => {
          console.log('Tâche ajoutée avec succès:', savedTask);
          // Ajouter la tâche au projet localement
          if (!project.tasks) project.tasks = [];
          project.tasks.push(savedTask);
          this.cd.detectChanges();
        },
        error: (error) => {
          console.error('Erreur lors de l\'ajout de la tâche:', error);
        }
      });
    } else {
      console.error('Projet sans ID, impossible de sauvegarder la tâche');
    }
  }

  // ... Le reste des méthodes reste inchangé ...
}
