import { Component } from '@angular/core';
import { Router } from '@angular/router';
import { AuthService } from '../../../services/auth.service';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent {
  email: string = '';
  password: string = '';
  errorMessage: string = '';

  constructor(private authService: AuthService, private router: Router) {}

  login() {
    this.errorMessage = '';

    if (!this.email || !this.password) {
      this.errorMessage = 'Veuillez remplir tous les champs';
      return;
    }

    this.authService.login({ email: this.email, password: this.password }).subscribe({
      next: (res: any) => {
        this.authService.saveToken(res.token); // Sauvegarde le token JWT
        this.router.navigate(['/dashboard']);  // Redirection après connexion
      },
      error: (err) => {
        this.errorMessage = err.error || 'Email ou mot de passe incorrect';
      }
    });
  }
}
<div class="login-container">
  <div class="login-card">
    <h2>Connexion</h2>

    <div *ngIf="errorMessage" class="alert alert-error">{{ errorMessage }}</div>

    <form (ngSubmit)="login()">
      <div class="form-group">
        <label>Email</label>
        <input type="email" [(ngModel)]="email" name="email" placeholder="Entrez votre email" required>
      </div>

      <div class="form-group">
        <label>Mot de passe</label>
        <input type="password" [(ngModel)]="password" name="password" placeholder="Entrez votre mot de passe" required>
      </div>

      <button type="submit" class="btn btn-primary">Se connecter</button>
    </form>

    <p class="text-center">
      Pas encore de compte ? <a routerLink="/register">S'inscrire</a>
    </p>
  </div>
</div>
