import { Component, OnInit, ChangeDetectorRef } from '@angular/core';
import { DashboardService, User, Project, Task } from '../../../services/dashboard.service';
import { Subject, forkJoin } from 'rxjs';
import { debounceTime, distinctUntilChanged } from 'rxjs/operators';

@Component({
  selector: 'app-dashboard',
  templateUrl: './dashboard.component.html',
  styleUrls: ['./dashboard.component.css']
})
export class DashboardComponent implements OnInit {

  currentUser: User = { id: 1, name: 'Admin', role: 'admin' };

  users: User[] = [];
  filteredUsers: User[] = [];
  projects: Project[] = [];

  // Formulaire projet
  newProjectName = '';
  newProjectDescription = '';
  assignedUserIds: number[] = [];
  newProjectNameError = false;
  newProjectDescriptionError = false;

  // Recherche utilisateur dynamique
  searchTerm$ = new Subject<string>();

  constructor(private dashboardService: DashboardService, private cd: ChangeDetectorRef) {}

  ngOnInit(): void {
    this.loadUsers();

    // Recherche utilisateur avec debounce
    this.searchTerm$.pipe(
      debounceTime(300),
      distinctUntilChanged()
    ).subscribe(term => this.filterUsers(term));
  }

  loadUsers() {
    this.dashboardService.getAllUsers().subscribe(users => {
      this.users = users.filter(u => u.role !== 'admin');
      this.filteredUsers = [...this.users];

      // Charger projets après avoir chargé utilisateurs
      this.loadProjects();
    });
  }

  loadProjects() {
    if (this.currentUser.role === 'admin') {
      // Récupérer tous les projets de tous les utilisateurs via forkJoin
      const observables = this.users.map(u => this.dashboardService.getProjectsByUser(u.name));
      forkJoin(observables).subscribe(results => {
        this.projects = results.flat();
        // ⚡ Forcer détection après update
        setTimeout(() => this.cd.detectChanges());
      });
    } else {
      this.dashboardService.getProjectsByUser(this.currentUser.name).subscribe(projects => {
        this.projects = projects;
      });
    }
  }

  isFormValid(): boolean {
    this.newProjectNameError = !this.newProjectName.trim();
    this.newProjectDescriptionError = !this.newProjectDescription.trim();
    return !this.newProjectNameError && !this.newProjectDescriptionError;
  }

  addProject() {
    if (this.currentUser.role !== 'admin' || !this.isFormValid()) return;

    const project: Project = {
      name: this.newProjectName.trim(),
      description: this.newProjectDescription.trim(),
      assignedTo: [...this.assignedUserIds],
      tasks: []
    };

    const observables = this.assignedUserIds.map(userId => {
      const user = this.users.find(u => u.id === userId);
      if (user) return this.dashboardService.createProject(user.name, project);
      return null;
    }).filter(obs => obs !== null) as any[];

    forkJoin(observables).subscribe(savedProjects => {
      this.projects.push(...savedProjects);
      setTimeout(() => this.cd.detectChanges());
    });

    this.newProjectName = '';
    this.newProjectDescription = '';
    this.assignedUserIds = [];
  }

  addTask(project: Project, taskTitle: string) {
    if (!taskTitle.trim()) return;
    const task: Task = { title: taskTitle.trim(), done: false };
    project.tasks.push(task);
  }

  toggleTask(task: Task) {
    task.done = !task.done;
  }

  removeTask(project: Project, task: Task) {
    project.tasks = project.tasks.filter(t => t !== task);
  }

  removeProject(project: Project) {
    if (this.currentUser.role !== 'admin' || !project.id) return;

    this.dashboardService.deleteProject(project.id).subscribe(() => {
      this.projects = this.projects.filter(p => p !== project);
    });
  }

  getAssignedUserNames(project: Project): string {
    return project.assignedTo
      .map(id => this.users.find(u => u.id === id)?.name)
      .filter(name => !!name)
      .join(', ');
  }

  onSearchUser(event: Event) {
    const target = event.target as HTMLInputElement;
    const term = target.value || '';
    this.searchTerm$.next(term);
  }

  filterUsers(term: string) {
    if (!term) {
      this.filteredUsers = [...this.users];
    } else {
      this.filteredUsers = this.users.filter(u => u.name.toLowerCase().includes(term.toLowerCase()));
    }
  }

  get visibleProjects(): Project[] {
    if (this.currentUser.role === 'admin') return this.projects;
    return this.projects.filter(p => p.assignedTo.includes(this.currentUser.id));
  }
}
<app-header-user></app-header-user>
<h2>Dashboard</h2>

<!-- Project creation form (admin only) -->
<div *ngIf="currentUser.role==='admin'" class="project-form">
  <input [(ngModel)]="newProjectName" placeholder="Project Name" [class.error]="newProjectNameError" />
  <input [(ngModel)]="newProjectDescription" placeholder="Description" [class.error]="newProjectDescriptionError" />

  <!-- Search users -->
  <input type="text" placeholder="Search user..." (input)="onSearchUser($event)" />

  <!-- Select users to assign -->
  <select multiple [(ngModel)]="assignedUserIds">
    <option *ngFor="let user of filteredUsers" [value]="user.id" [disabled]="user.role==='admin'">{{user.name}}</option>
  </select>

  <button (click)="addProject()" [disabled]="!isFormValid()">Create Project</button>
</div>

<div class="error-message" *ngIf="newProjectNameError || newProjectDescriptionError">
  Fields cannot be empty!
</div>

<!-- Project list -->
<div class="project-list">
  <div *ngFor="let project of visibleProjects" class="project-card">
    <h3>{{ project.name }}</h3>
    <p>{{ project.description }}</p>

    <p *ngIf="currentUser.role==='admin'">
      Assigned to: {{ getAssignedUserNames(project) }}
    </p>

    <!-- Add task -->
    <div class="task-form">
      <input #taskInput placeholder="New Task" />
      <button (click)="addTask(project, taskInput.value); taskInput.value=''">Add Task</button>
    </div>

    <!-- Task list -->
    <ul class="task-list">
      <li *ngFor="let task of project.tasks">
        <input type="checkbox" [(ngModel)]="task.done" (change)="toggleTask(task)" />
        <span [style.text-decoration]="task.done ? 'line-through' : 'none'">{{ task.title }}</span>
        <button *ngIf="currentUser.role==='admin'" (click)="removeTask(project, task)" class="delete-btn">×</button>
      </li>
    </ul>

    <button *ngIf="currentUser.role==='admin'" (click)="removeProject(project)" class="delete-btn">Delete Project</button>
  </div>
</div>
