import { Component, OnInit } from '@angular/core';
import { Project, Task, User, DashboardClientService } from '../../../services/dashboard-client.service';

@Component({
  selector: 'app-dashboard-client',
  templateUrl: './dashboard-client.component.html',
  styleUrls: ['./dashboard-client.component.css']
})
export class DashboardClientComponent implements OnInit {
  currentUser: User | null = null; // utilisateur connect√©
  projects: Project[] = [];
  users: User[] = [];
  loadingProjects: boolean = true;
  loadingUsers: boolean = true;
  errorMessage: string = '';

  constructor(private dashboardClientService: DashboardClientService) {}

 ngOnInit(): void {
  // R√©cup√©rer l'utilisateur connect√© depuis le localStorage
  const userData = localStorage.getItem('currentUser');
  if (userData) {
    this.currentUser = JSON.parse(userData);
  }

  // V√©rifier que currentUser et email existent
  if (!this.currentUser?.email) {
    console.error('Utilisateur non connect√© ou email manquant.');
    this.errorMessage = "Vous n'√™tes pas connect√© ou l'email est manquant.";
    this.loadingProjects = false;
    this.loadingUsers = false;
    return;
  }

  // Charger les projets assign√©s √† l'utilisateur
  this.dashboardClientService.getProjectsByUserEmail(this.currentUser.email)
    .subscribe({
      next: (projects) => {
        this.projects = projects;
        this.loadingProjects = false;
      },
      error: (err) => {
        console.error('Erreur lors du chargement des projets :', err);
        this.errorMessage = "Impossible de charger les projets.";
        this.loadingProjects = false;
      }
    });

  // Charger tous les utilisateurs
  this.dashboardClientService.getAllUsers()
    .subscribe({
      next: (users) => {
        this.users = users;
        this.loadingUsers = false;
      },
      error: (err) => {
        console.error('Erreur lors du chargement des utilisateurs :', err);
        this.errorMessage = "Impossible de charger les utilisateurs.";
        this.loadingUsers = false;
      }
    });
}


  addTask(project: Project, title: string) {
    if (!title.trim()) return;
    if (!project.id) {
      console.error('ID du projet manquant pour ajouter une t√¢che.');
      return;
    }

    const newTask: Task = { title, done: false };
    this.dashboardClientService.addTaskToProject(project.id, newTask)
      .subscribe({
        next: (savedTask: Task) => {
          if (!project.tasks) project.tasks = [];
          project.tasks.push(savedTask);
        },
        error: (err) => console.error('Erreur ajout t√¢che :', err)
      });
  }

  toggleTask(task: Task) {
    if (!task.id) {
      console.error('ID de la t√¢che manquant pour toggle.');
      return;
    }

    this.dashboardClientService.toggleTaskStatus(task.id)
      .subscribe({
        next: (updatedTask: Task) => {
          task.done = updatedTask.done;
        },
        error: (err) => console.error('Erreur toggle t√¢che :', err)
      });
  }

  getUserName(userId: number): string {
    const user = this.users.find(u => u.id === userId);
    return user ? user.name : 'Utilisateur inconnu';
  }
}
