import { Component, OnInit } from '@angular/core';
import { User, Project, Task } from '../dashboard.model';

@Component({
  selector: 'app-dashboard',
  templateUrl: './dashboard.component.html',
  styleUrls: ['./dashboard.component.css'],
})
export class DashboardComponent implements OnInit {
  currentUser: User = { id: 2, name: 'Client1', role: 'client' };

  users: User[] = [
    { id: 1, name: 'Admin', role: 'admin' },
    { id: 2, name: 'Client1', role: 'client' },
    { id: 3, name: 'Client2', role: 'client' },
  ];

  projects: Project[] = [];
  nextProjectId = 1;
  nextTaskId = 1;

  newProjectName = '';
  newProjectDescription = '';
  assignedUserIds: number[] = [];
  newProjectNameError = false;
  newProjectDescriptionError = false;

  ngOnInit(): void {
    this.projects = [
      { id: 1, name: 'Projet A', description: 'Description A', assignedTo: [2], tasks: [] },
      { id: 2, name: 'Projet B', description: 'Description B', assignedTo: [3], tasks: [] },
    ];
  }

  isFormValid(): boolean {
    this.newProjectNameError = !this.newProjectName.trim();
    this.newProjectDescriptionError = !this.newProjectDescription.trim();
    return !this.newProjectNameError && !this.newProjectDescriptionError;
  }

  addProject() {
    if (this.currentUser.role !== 'admin') return;
    if (!this.isFormValid()) return;

    const project: Project = {
      id: this.nextProjectId++,
      name: this.newProjectName.trim(),
      description: this.newProjectDescription.trim(),
      assignedTo: [...this.assignedUserIds],
      tasks: [],
    };
    this.projects.push(project);
    this.newProjectName = '';
    this.newProjectDescription = '';
    this.assignedUserIds = [];
  }

  addTask(project: Project, taskTitle: string) {
    if (!taskTitle.trim()) return;
    project.tasks.push({ id: this.nextTaskId++, title: taskTitle.trim(), done: false });
  }

  toggleTask(task: Task) {
    task.done = !task.done;
  }

  removeTask(project: Project, task: Task) {
    project.tasks = project.tasks.filter(t => t.id !== task.id);
  }

  removeProject(project: Project) {
    if (this.currentUser.role !== 'admin') return;
    this.projects = this.projects.filter(p => p.id !== project.id);
  }

  get visibleProjects(): Project[] {
    return this.currentUser.role === 'admin'
      ? this.projects
      : this.projects.filter(p => p.assignedTo.includes(this.currentUser.id));
  }

  // Fonction pour obtenir le nom de l'utilisateur par id
  getUserName(id: number): string {
    const user = this.users.find(u => u.id === id);
    return user ? user.name : 'Inconnu';
  }
}
<h2>Dashboard</h2>

<!-- Formulaire création projet (admin uniquement) -->
<div *ngIf="currentUser.role==='admin'" class="project-form">
  <input [(ngModel)]="newProjectName" placeholder="Nom du projet" [class.error]="newProjectNameError" />
  <input [(ngModel)]="newProjectDescription" placeholder="Description" [class.error]="newProjectDescriptionError" />

  <select multiple [(ngModel)]="assignedUserIds">
    <option *ngFor="let user of users" [value]="user.id" [disabled]="user.role==='admin'">{{ user.name }}</option>
  </select>

  <button (click)="addProject()" [disabled]="!isFormValid()">Créer projet</button>
</div>

<div class="error-message" *ngIf="newProjectNameError || newProjectDescriptionError">
  Les champs ne peuvent pas être vides !
</div>

<!-- Liste des projets -->
<div class="project-list">
  <div *ngFor="let project of visibleProjects" class="project-card">
    <h3>{{ project.name }}</h3>
    <p>{{ project.description }}</p>

    <p *ngIf="currentUser.role==='admin'">
      Assigné à:
      <span *ngFor="let id of project.assignedTo">{{ getUserName(id) }}</span>
    </p>

    <!-- Ajouter tâche -->
    <div class="task-form">
      <input #taskInput placeholder="Nouvelle tâche" />
      <button (click)="addTask(project, taskInput.value); taskInput.value = ''">Ajouter tâche</button>
    </div>

    <!-- Liste des tâches -->
    <ul class="task-list">
      <li *ngFor="let task of project.tasks">
        <input type="checkbox" [(ngModel)]="task.done" (change)="toggleTask(task)" />
        <span [style.text-decoration]="task.done ? 'line-through' : 'none'">{{ task.title }}</span>
        <button *ngIf="currentUser.role==='admin'" (click)="removeTask(project, task)" class="delete-btn">×</button>
      </li>
    </ul>

    <button *ngIf="currentUser.role==='admin'" (click)="removeProject(project)" class="delete-btn">Supprimer projet</button>
  </div>
</div>
