import { Component, OnInit, ChangeDetectorRef } from '@angular/core';
import { DashboardService, Project, Task } from '../../../services/dashboard.service';
import { UserService, User } from '../../../services/user.service';
import { Subject, forkJoin } from 'rxjs';
import { debounceTime, distinctUntilChanged } from 'rxjs/operators';

@Component({
  selector: 'app-dashboard',
  templateUrl: './dashboard.component.html',
  styleUrls: ['./dashboard.component.css']
})
export class DashboardComponent implements OnInit {

  currentUser: User = { id: 1, nom: 'Admin', prenom: 'Admin', email: 'admin@example.com', role: 'admin' };

  users: User[] = [];
  filteredUsers: User[] = [];
  projects: Project[] = [];

  newProjectTitle = '';  // Changé de newProjectName à newProjectTitle
  newProjectDescription = '';
  assignedUserId: number | null = null;  // Changé pour un seul utilisateur
  newProjectTitleError = false;
  newProjectDescriptionError = false;

  searchTerm$ = new Subject<string>();

  constructor(
    private dashboardService: DashboardService,
    private userService: UserService,
    private cd: ChangeDetectorRef
  ) {}

  ngOnInit(): void {
    this.loadUsers();

    this.searchTerm$.pipe(
      debounceTime(300),
      distinctUntilChanged()
    ).subscribe(term => this.filterUsers(term));
  }

  loadUsers() {
    this.userService.getAllUsers().subscribe({
      next: (users) => {
        this.users = users.filter(u => u.role !== 'admin');
        this.filteredUsers = [...this.users];
        this.loadProjects();
      },
      error: (error) => {
        console.error('Erreur lors du chargement des utilisateurs:', error);
      }
    });
  }

  loadProjects() {
    if (this.currentUser.role === 'admin') {
      // Pour les admins, charger TOUS les projets
      this.dashboardService.getAllProjects().subscribe({
        next: (projects) => {
          console.log('Projets chargés depuis getAllProjects:', projects);
          this.projects = projects || [];
          this.cd.detectChanges();
        },
        error: (error) => {
          console.error('Erreur lors du chargement des projets:', error);
          this.projects = [];
          this.cd.detectChanges();
        }
      });
    } else {
      // Pour les utilisateurs normaux, charger seulement leurs projets
      this.dashboardService.getProjectsByUser(this.currentUser.email).subscribe({
        next: (projects) => {
          console.log('Projets chargés pour l\'utilisateur:', projects);
          this.projects = projects || [];
          this.cd.detectChanges();
        },
        error: (error) => {
          console.error('Erreur lors du chargement des projets utilisateur:', error);
          this.projects = [];
          this.cd.detectChanges();
        }
      });
    }
  }

  isFormValid(): boolean {
    return !!this.newProjectTitle.trim() && 
           !!this.newProjectDescription.trim() && 
           this.assignedUserId !== null;
  }

  validateForm() {
    this.newProjectTitleError = !this.newProjectTitle.trim();
    this.newProjectDescriptionError = !this.newProjectDescription.trim();
  }

  addProject() {
    this.validateForm();
    if (this.currentUser.role !== 'admin' || !this.isFormValid()) return;

    const project: Project = {
      title: this.newProjectTitle.trim(),  // Changé de name à title
      description: this.newProjectDescription.trim(),
      assignedTo: this.assignedUserId ? [this.assignedUserId] : [],  // Adapté pour un seul utilisateur
      tasks: []
    };

    const user = this.users.find(u => u.id === this.assignedUserId);

    if (user) {
      this.dashboardService.createProject(user.email, project).subscribe({
        next: (savedProject) => {
          this.projects.push(savedProject);
          this.cd.detectChanges();
          this.resetForm();
          console.log('Projet créé avec succès:', savedProject);
        },
        error: (error) => {
          console.error('Erreur lors de la création du projet:', error);
        }
      });
    }
  }

  resetForm() {
    this.newProjectTitle = '';
    this.newProjectDescription = '';
    this.assignedUserId = null;
    this.newProjectTitleError = false;
    this.newProjectDescriptionError = false;
  }

  addTask(project: Project, taskTitle: string) {
    if (!taskTitle || !taskTitle.trim()) {
      console.log('Titre de tâche vide, abandon');
      return;
    }

    const task: Task = { 
      title: taskTitle.trim(), 
      done: false 
    };

    // Ajouter la tâche localement d'abord
    if (!project.tasks) project.tasks = [];
    project.tasks.push(task);

    // Puis mettre à jour sur le serveur
    if (project.id) {
      this.dashboardService.updateProject(project.id, project).subscribe({
        next: (updatedProject) => {
          console.log('Tâche ajoutée avec succès:', task);
          // Optionnel: remplacer le projet local par la version serveur
          const index = this.projects.findIndex(p => p.id === project.id);
          if (index !== -1) {
            this.projects[index] = updatedProject;
          }
          this.cd.detectChanges();
        },
        error: (error) => {
          console.error('Erreur lors de l\'ajout de la tâche:', error);
          // Rollback en cas d'erreur
          project.tasks = project.tasks.filter(t => t !== task);
          this.cd.detectChanges();
        }
      });
    } else {
      console.error('Projet sans ID, impossible de sauvegarder la tâche');
    }
  }

  toggleTask(task: Task, project: Project) {
    task.done = !task.done;

    if (project.id) {
      this.dashboardService.updateProject(project.id, project).subscribe({
        next: () => {
          console.log('Statut de tâche mis à jour:', task);
        },
        error: (error) => {
          console.error('Erreur lors de la mise à jour de la tâche:', error);
          // Rollback
          task.done = !task.done;
          this.cd.detectChanges();
        }
      });
    }
  }

  removeTask(project: Project, task: Task) {
    const originalTasks = [...project.tasks];
    project.tasks = project.tasks.filter(t => t !== task);

    if (project.id) {
      this.dashboardService.updateProject(project.id, project).subscribe({
        next: () => {
          console.log('Tâche supprimée avec succès');
          this.cd.detectChanges();
        },
        error: (error) => {
          console.error('Erreur lors de la suppression de la tâche:', error);
          // Rollback
          project.tasks = originalTasks;
          this.cd.detectChanges();
        }
      });
    }
  }

  removeProject(project: Project) {
    if (!project.id) return;

    this.dashboardService.deleteProject(project.id).subscribe({
      next: () => {
        this.projects = this.projects.filter(p => p !== project);
        this.cd.detectChanges();
        console.log('Projet supprimé avec succès');
      },
      error: (error) => {
        console.error('Erreur lors de la suppression du projet:', error);
      }
    });
  }

  getAssignedUserNames(project: Project): string {
    if (!project.assignedTo || project.assignedTo.length === 0) return 'Non assigné';

    return project.assignedTo
      .map(id => {
        const user = this.users.find(u => u.id === id);
        return user ? `${user.nom} ${user.prenom} (${user.email})` : '';
      })
      .filter(Boolean)
      .join(', ');
  }

  onSearchUser(event: Event) {
    const term = (event.target as HTMLInputElement).value || '';
    this.searchTerm$.next(term);
  }

  filterUsers(term: string) {
    this.filteredUsers = term
      ? this.users.filter(u => 
          u.nom.toLowerCase().includes(term.toLowerCase()) ||
          u.prenom.toLowerCase().includes(term.toLowerCase()) ||
          u.email.toLowerCase().includes(term.toLowerCase())
        )
      : [...this.users];
  }

  get visibleProjects(): Project[] {
    if (this.currentUser.role === 'admin') {
      return this.projects;
    }
    return this.projects.filter(p => p.assignedTo && p.assignedTo.includes(this.currentUser.id!));
  }

  get hasFormErrors(): boolean {
    return this.newProjectTitleError || this.newProjectDescriptionError;
  }

  // Méthodes de tracking pour optimiser le rendu
  trackByProjectId(index: number, project: Project): any {
    return project.id || index;
  }

  trackByTaskTitle(index: number, task: Task): any {
    return task.title || index;
  }

  // Gérer l'ajout de tâche avec la touche Entrée
  onTaskInputKeyPress(event: KeyboardEvent, project: Project, input: HTMLInputElement) {
    if (event.key === 'Enter' && input.value.trim()) {
      this.addTask(project, input.value);
      input.value = '';
    }
  }

  // Méthode de débogage pour vérifier l'état
  debugProjects() {
    console.log('=== DEBUG PROJECTS ===');
    console.log('Nombre total de projets:', this.projects.length);
    console.log('Projets visibles:', this.visibleProjects.length);
    console.log('Rôle utilisateur:', this.currentUser.role);
    console.log('ID utilisateur:', this.currentUser.id);
    console.log('Tous les projets:', this.projects);
    console.log('Projets visibles détail:', this.visibleProjects);
    this.projects.forEach((project, index) => {
      console.log(`Projet ${index}:`, {
        id: project.id,
        title: project.title,  // Changé de name à title
        assignedTo: project.assignedTo,
        tasks: project.tasks?.length || 0
      });
    });
  }
}
