// src/app/services/dashboard.service.ts
import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Observable } from 'rxjs';
import { environment } from '../environments/environment';

export interface User {
  id: number;
  name: string;
  role: string; // "admin" ou "client"
}

export interface Task {
  id?: number;
  title: string;
  done: boolean;
}

export interface Project {
  id?: number;
  name: string;
  description: string;
  assignedTo: number[]; 
  tasks: Task[];
}

@Injectable({
  providedIn: 'root'
})
export class DashboardService {
  private apiUsers = `${environment.API_URL}/users`;
  private apiProjects = `${environment.API_URL}/projects`;

  constructor(private http: HttpClient) {}

  // ðŸ”¹ RÃ©cupÃ©rer tous les utilisateurs
  getAllUsers(): Observable<User[]> {
    return this.http.get<User[]>(this.apiUsers);
  }

  // ðŸ”¹ RÃ©cupÃ©rer les projets pour un utilisateur
  getProjectsByUser(email: string): Observable<Project[]> {
    return this.http.get<Project[]>(`${this.apiProjects}/user/${email}`);
  }

  // ðŸ”¹ CrÃ©er un projet pour un utilisateur
  createProject(email: string, project: Project): Observable<Project> {
    return this.http.post<Project>(`${this.apiProjects}/user/${email}`, project);
  }

  // ðŸ”¹ Mettre Ã  jour un projet
  updateProject(projectId: number, project: Project): Observable<Project> {
    return this.http.put<Project>(`${this.apiProjects}/${projectId}`, project);
  }

  // ðŸ”¹ Supprimer un projet
  deleteProject(projectId: number): Observable<void> {
    return this.http.delete<void>(`${this.apiProjects}/${projectId}`);
  }

  // ðŸ”¹ Rechercher utilisateurs par nom (pour assigner projet)
  searchUsersByName(name: string): Observable<User[]> {
    return this.http.get<User[]>(`${this.apiUsers}?name=${name}`);
  }
}
