import { Component } from '@angular/core';
import { Router } from '@angular/router';
import { UserService, User } from '../../../services/user.service';

@Component({
  selector: 'app-register',
  templateUrl: './register.component.html',
  styleUrls: ['./register.component.css']
})
export class RegisterComponent {
  nom: string = '';
  prenom: string = '';
  email: string = '';
  password: string = '';
  confirmPassword: string = '';
  errorMessage: string = '';
  successMessage: string = '';

  constructor(private userService: UserService, private router: Router) {}

  register() {
    this.errorMessage = '';
    this.successMessage = '';

    // Validation basique
    if (!this.nom || !this.prenom || !this.email || !this.password || !this.confirmPassword) {
      this.errorMessage = 'Veuillez remplir tous les champs';
      return;
    }

    if (this.password !== this.confirmPassword) {
      this.errorMessage = 'Les mots de passe ne correspondent pas';
      return;
    }

    const newUser: User = {
      nom: this.nom,
      prenom: this.prenom,
      email: this.email,
      password: this.password,
      role: 'eleve' // rôle par défaut
    };

    this.userService.createUser(newUser).subscribe({
      next: (user) => {
        this.successMessage = 'Compte créé avec succès !';
        this.router.navigate(['/login']);
      },
      error: (err) => {
        if (err.status === 400) {
          this.errorMessage = 'Cette adresse e-mail est déjà utilisée.';
        } else {
          this.errorMessage = 'Erreur lors de la création du compte. Veuillez réessayer.';
        }
      }
    });
  }
}

<div class="register-container">
  <div class="register-card">
    <h2 class="register-title">Créer un compte</h2>
    <p class="register-subtitle">Inscrivez-vous pour commencer</p>

    <div *ngIf="errorMessage" class="alert alert-danger">{{ errorMessage }}</div>
    <div *ngIf="successMessage" class="alert alert-success">{{ successMessage }}</div>

    <form (ngSubmit)="register()" #registerForm="ngForm">
      <div class="form-group">
        <label for="register-nom">Nom</label>
        <input type="text" id="register-nom" name="nom" [(ngModel)]="nom" placeholder="Votre nom" required>
      </div>

      <div class="form-group">
        <label for="register-prenom">Prénom</label>
        <input type="text" id="register-prenom" name="prenom" [(ngModel)]="prenom" placeholder="Votre prénom" required>
      </div>

      <div class="form-group">
        <label for="register-email">Email</label>
        <input type="email" id="register-email" name="email" [(ngModel)]="email" placeholder="Votre email" required>
      </div>

      <div class="form-group">
        <label for="register-password">Mot de passe</label>
        <input type="password" id="register-password" name="password" [(ngModel)]="password" placeholder="Votre mot de passe" required>
      </div>

      <div class="form-group">
        <label for="register-confirm">Confirmer le mot de passe</label>
        <input type="password" id="register-confirm" name="confirmPassword" [(ngModel)]="confirmPassword" placeholder="Confirmez le mot de passe" required>
      </div>

      <button type="submit" class="btn btn-primary">Créer le compte</button>
    </form>

    <p class="text-center mt-2">
      Vous avez déjà un compte ? 
      <a routerLink="/login" class="login-link">Se connecter</a>
    </p>
  </div>
</div>
