// dashboard.component.ts
import { Component, OnInit } from '@angular/core';

interface Project {
  id: number;
  name: string;
  description: string;
  tasks: Task[];
}

interface Task {
  id: number;
  title: string;
  done: boolean;
}

@Component({
  selector: 'app-dashboard',
  templateUrl: './dashboard.component.html',
})
export class DashboardComponent implements OnInit {
  projects: Project[] = [];
  nextProjectId = 1;
  nextTaskId = 1;

  newProjectName = '';
  newProjectDescription = '';

  constructor() {}

  ngOnInit(): void {}

  addProject() {
    const project: Project = {
      id: this.nextProjectId++,
      name: this.newProjectName,
      description: this.newProjectDescription,
      tasks: []
    };
    this.projects.push(project);
    this.newProjectName = '';
    this.newProjectDescription = '';
  }

  addTask(project: Project, taskTitle: string) {
    const task: Task = { id: this.nextTaskId++, title: taskTitle, done: false };
    project.tasks.push(task);
  }

  toggleTask(task: Task) {
    task.done = !task.done;
  }
}
<div class="dashboard-container">
  <h2>Welcome to Your Dashboard!</h2>
  <p>Here are your projects and tasks overview:</p>
  <div class="dashboard-links">
    <a routerLink="/projects" class="btn">View Projects</a>
    <a routerLink="/tasks" class="btn secondary">View Tasks</a>
  </div>
</div>
