import { Component } from '@angular/core';
import { Router } from '@angular/router';
import { AuthService } from '../../../services/auth.service';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent {
  email: string = '';
  password: string = '';
  errorMessage: string = '';

  constructor(private authService: AuthService, private router: Router) {}

  login() {
    this.errorMessage = '';

    if (!this.email || !this.password) {
      this.errorMessage = 'Veuillez remplir tous les champs';
      return;
    }

    this.authService.login({ email: this.email, password: this.password }).subscribe({
      next: (res: any) => {
        this.authService.saveToken(res.token); // Sauvegarde le token JWT
        this.router.navigate(['/dashboard']);  // Redirection après connexion
      },
      error: (err) => {
        this.errorMessage = err.error || 'Email ou mot de passe incorrect';
      }
    });
  }
}
<div class="auth-container">
  <h2>Login</h2>
  <form (ngSubmit)="login($event)">
    <div class="form-group">
      <label>Email</label>
      <input type="email" placeholder="Enter your email" [(ngModel)]="email" name="email" required>
    </div>
    <div class="form-group">
      <label>Password</label>
      <input type="password" placeholder="Enter your password" [(ngModel)]="password" name="password" required>
    </div>
    <button type="submit" class="btn">Login</button>
  </form>
  <p class="switch-link">
    Don't have an account? <a routerLink="/register">Register</a>
  </p>
</div>
