// dashboard.component.ts
import { Component, OnInit } from '@angular/core';

interface Project {
  id: number;
  name: string;
  description: string;
  tasks: Task[];
}

interface Task {
  id: number;
  title: string;
  done: boolean;
}

@Component({
  selector: 'app-dashboard',
  templateUrl: './dashboard.component.html',
})
export class DashboardComponent implements OnInit {
  projects: Project[] = [];
  nextProjectId = 1;
  nextTaskId = 1;

  newProjectName = '';
  newProjectDescription = '';

  constructor() {}

  ngOnInit(): void {}

  addProject() {
    const project: Project = {
      id: this.nextProjectId++,
      name: this.newProjectName,
      description: this.newProjectDescription,
      tasks: []
    };
    this.projects.push(project);
    this.newProjectName = '';
    this.newProjectDescription = '';
  }

  addTask(project: Project, taskTitle: string) {
    const task: Task = { id: this.nextTaskId++, title: taskTitle, done: false };
    project.tasks.push(task);
  }

  toggleTask(task: Task) {
    task.done = !task.done;
  }
}
<h2>Mes Projets</h2>

<!-- Formulaire création projet -->
<div>
  <input [(ngModel)]="newProjectName" placeholder="Nom du projet" />
  <input [(ngModel)]="newProjectDescription" placeholder="Description" />
  <button (click)="addProject()">Créer projet</button>
</div>

<!-- Liste des projets -->
<div *ngFor="let project of projects" style="border:1px solid #ccc; padding:10px; margin:10px 0;">
  <h3>{{ project.name }}</h3>
  <p>{{ project.description }}</p>

  <!-- Ajouter tâche -->
  <input #taskInput placeholder="Nouvelle tâche" />
  <button (click)="addTask(project, taskInput.value); taskInput.value=''">Ajouter tâche</button>

  <!-- Liste tâches -->
  <ul>
    <li *ngFor="let task of project.tasks">
      <input type="checkbox" [(ngModel)]="task.done" (change)="toggleTask(task)" />
      <span [style.text-decoration]="task.done ? 'line-through' : 'none'">{{ task.title }}</span>
    </li>
  </ul>
</div>
