import { Component } from '@angular/core';
import { Router } from '@angular/router';

@Component({
  selector: 'app-register',
  templateUrl: './register.component.html',
  styleUrls: ['./register.component.css']
})
export class RegisterComponent {
  name: string = '';
  email: string = '';
  password: string = '';
  confirmPassword: string = '';
  errorMessage: string = '';

  constructor(private router: Router) {}

  register() {
    if (!this.name || !this.email || !this.password || !this.confirmPassword) {
      this.errorMessage = 'Please fill in all fields';
      return;
    }

    if (this.password !== this.confirmPassword) {
      this.errorMessage = 'Passwords do not match';
      return;
    }

    // Simulate registration
    alert('Account created successfully!');
    this.router.navigate(['/login']);
  }
}
<div class="register-container">
  <div class="register-card">
    <h2 class="register-title">Create Account</h2>
    <p class="register-subtitle">Sign up to get started</p>

    <div id="register-error" class="alert alert-error" *ngIf="errorMessage">{{ errorMessage }}</div>

    <form (ngSubmit)="register()" #registerForm="ngForm">
      <div class="form-group">
        <label for="register-name">Full Name</label>
        <input type="text" id="register-name" name="name" [(ngModel)]="name" placeholder="Enter your full name" required>
      </div>

      <div class="form-group">
        <label for="register-email">Email</label>
        <input type="email" id="register-email" name="email" [(ngModel)]="email" placeholder="Enter your email" required>
      </div>

      <div class="form-group">
        <label for="register-password">Password</label>
        <input type="password" id="register-password" name="password" [(ngModel)]="password" placeholder="Enter your password" required>
      </div>

      <div class="form-group">
        <label for="register-confirm">Confirm Password</label>
        <input type="password" id="register-confirm" name="confirmPassword" [(ngModel)]="confirmPassword" placeholder="Confirm your password" required>
      </div>

      <button type="submit" class="btn btn-primary">Create Account</button>
    </form>

    <p class="text-center">
      Already have an account?
      <a routerLink="/login" class="login-link">Sign in</a>
    </p>
  </div>
</div>
