import { Component, OnInit } from '@angular/core';

interface Project {
  id: number;
  name: string;
  description: string;
  tasks: Task[];
}

interface Task {
  id: number;
  title: string;
  done: boolean;
}

@Component({
  selector: 'app-dashboard',
  templateUrl: './dashboard.component.html',
  styleUrls: ['./dashboard.component.css'],
})
export class DashboardComponent implements OnInit {
  projects: Project[] = [];
  nextProjectId = 1;
  nextTaskId = 1;

  newProjectName = '';
  newProjectDescription = '';
  newProjectNameError = false;
  newProjectDescriptionError = false;

  constructor() {}

  ngOnInit(): void {
    // Initialisation si besoin (ex. chargement depuis un service)
  }

  isFormValid(): boolean {
    this.newProjectNameError = !this.newProjectName.trim();
    this.newProjectDescriptionError = !this.newProjectDescription.trim();
    return !this.newProjectNameError && !this.newProjectDescriptionError;
  }

  addProject() {
    if (!this.isFormValid()) return;

    const project: Project = {
      id: this.nextProjectId++,
      name: this.newProjectName.trim(),
      description: this.newProjectDescription.trim(),
      tasks: [],
    };
    this.projects.push(project);
    this.newProjectName = '';
    this.newProjectDescription = '';
  }

  addTask(project: Project, taskTitle: string) {
    if (!taskTitle.trim()) return;
    const task: Task = { id: this.nextTaskId++, title: taskTitle.trim(), done: false };
    project.tasks.push(task);
  }

  toggleTask(task: Task) {
    task.done = !task.done;
  }

  removeTask(project: Project, task: Task) {
    project.tasks = project.tasks.filter(t => t.id !== task.id);
  }

  removeProject(project: Project) {
    this.projects = this.projects.filter(p => p.id !== project.id);
  }
}
