          this.loadTasksForAllProjects();
        },
        error: error => {
          this.projects = [];
          this.cd.detectChanges();
        }
      });
    } else {
      this.dashboardService.getProjectsByUser(this.currentUser.email).subscribe({
        next: projects => {
          this.projects = projects || [];
          this.loadTasksForAllProjects();
        },
        error: error => {
          this.projects = [];
          this.cd.detectChanges();
        }
      });
    }
