    if (taskObservables.length > 0) {
      forkJoin(taskObservables).subscribe({
        next: tasksArrays => {
          tasksArrays.forEach((tasks, index) => {
            this.projects[index].tasks = tasks;
          });
          this.cd.detectChanges();
        },
        error: error => {
          this.cd.detectChanges();
        }
      });
